const path = require('path');
const fs = require('fs');

module.exports = () => {
  const filePath = path.join(process.cwd(), 'tools/aimakerc.js');
  if (fs.existsSync(filePath)) {
    try {
      return require(filePath);
    } catch (e) {
      console.log(e);
    }
  } else {
    // .aimakerc is not exist
    // console.log('.aimakerc is not exist');
    return {};
  }
  return {};
};
